import "../App.css";

import React from "react";
import Axios from "axios";

import { useState } from "react";
import { Link } from "react-router-dom";


const AddStudentPage = () => {
    const [name, setName] = useState("");
    const [age, setAge] = useState(0);
    const [country, setCountry] = useState("");
    const [position, setPosition] = useState("");
    const [wage, setWage] = useState(0);

    const [newWage, setNewWage] = useState(0);

    const [employeeList, setEmployeeList] = useState([]);


    const addEmployee = () => {
        Axios.post("http://localhost:3001/create", {
        name: name,
        age: age,
        country: country,
        position: position,
        wage: wage,
        }).then(() => {
        setEmployeeList([
            ...employeeList,
            {
            name: name,
            age: age,
            country: country,
            position: position,
            wage: wage,
            },
        ]);
        });
    };


    return (
        <div className="App">
        <div className="information">
            <h1>Add new Student</h1>

            <label>Name:</label>
            <input
            type="text" placeholder="Enter Name"
            onChange={(event) => {
                setName(event.target.value);
            }}
            />

            <label>Age:</label>
            <input
            type="number" placeholder="Enter Age"
            onChange={(event) => {
                setAge(event.target.value);
            }}
            />

            <label>Country:</label>
            <input
            type="text" placeholder="Enter Country"
            onChange={(event) => {
                setCountry(event.target.value);
            }}
            />

            <label>Position:</label>
            <input
            type="text" placeholder="Enter Position"
            onChange={(event) => {
                setPosition(event.target.value);
            }}
            />

            <label>Wage (year):</label>
            <input
            type="number" placeholder="Enter Wage"
            onChange={(event) => {
                setWage(event.target.value);
            }}
            />


            <button onClick={addEmployee}><Link to="/employee-list">Add</Link></button>
        </div>

        </div>
    );
}

export default AddStudentPage;
